import { Table, InputNumber } from 'antd';
import { useState, useEffect, useCallback } from 'react';

export const Home = () => {
	const [dataSource, setDataSource] = useState([]);

	const parseData = useCallback((data) => {
		const modifiedData = data.map((ticket) => {
			const ticketObject = {
				id: ticket.id,
				name: ticket.title,
				type: ticket.type,
				date: new Date(ticket.releaseDate).toLocaleDateString('es-ES'),
				releaseDate: ticket.releaseDate,
				price: ticket.price,
				units: 0,
			};

			return ticketObject;
		});

		return modifiedData;
	}, []);

	const fetchData = useCallback(async () => {
		const request = await fetch(
			'https://my-json-server.typicode.com/davidan90/demo/tickets'
		);
		const response = await request.json();
		const result = parseData(response); // [{id, name, date, units, etc}]
		setDataSource(result);
	}, []);

	useEffect(() => {
		fetchData();
		// const result = parseData(dataSource); // [{id, name, date, units, etc}]
		// setDataSource(result);
	}, [fetchData]);
	//                           7     {id, name, date, unit, price}
	const handleOnChange = (value, rowData) => {
		const dataCopy = { ...dataSource };
		const elementIndex = dataCopy.findIndex((el) => el.id === rowData.id);

		// cuando no encuentra el elemento el index es -1
		if (elementIndex > -1) {
			//{id, name, date, units, etc}
			dataCopy[elementIndex].units = value;
		}
	};

	const columns = [
		{
			title: 'Name',
			dataIndex: 'name',
			key: 'name',
		},
		{
			title: 'Type',
			dataIndex: 'type',
			key: 'type',
		},
		{
			title: 'Release Date',
			dataIndex: 'date',
			key: 'date',
			defaultSortOrder: 'descend',
			sorter: (a, b) => a.releaseDate - b.releaseDate,
		},
		{
			title: 'Unit Selector',
			dataIndex: 'units',
			key: 'units',
			render: (units, rowData) => {
				return (
					<InputNumber
						min={0}
						max={20}
						defaultValue={units}
						onChange={(value) => handleOnChange(value, rowData)}
					/>
				);
			},
		},
		{
			title: 'Price',
			dataIndex: 'price',
			key: 'price',
		},
	];

	return (
		<>
			<Table dataSource={dataSource} columns={columns} pagination={false} />
		</>
	);
};
